import React, { Component } from 'react'
import 'react-pro-sidebar/dist/css/styles.css';
import {
    ProSidebar,
    Menu,
    MenuItem,
    SubMenu,
    SidebarHeader,
    SidebarFooter,
    SidebarContent,
  } from "react-pro-sidebar";
  import "./ViewExp.css";
  import { FaList, FaRegHeart } from "react-icons/fa";
  import { FiHome, FiLogOut, FiArrowLeftCircle, FiArrowRightCircle } from "react-icons/fi";
  import { RiPencilLine } from "react-icons/ri";
  import { BiCog } from "react-icons/bi";
  
const axios = require('axios');


class ViewExp extends Component{
    constructor(){
        super();
        this.state = {
            dets:[],
            menuCollapse:false,
        }
    }
    menuIconClick = () => {
        //condition checking to change state from true to false and vice versa
        this.state.menuCollapse ? this.setMenuCollapse(false) : this.setMenuCollapse(true);
    };
    setMenuCollapse = (bolval) =>{
        this.setState({dets:this.state.dets,menuCollapse:bolval})
    }
    setMenuCollapse = this.setMenuCollapse.bind(this);
    menuIconClick = this.menuCollapse.bind(this);
    componentDidMount(){
        var self = this
        axios.get('http://localhost:4000/api/experiences/getallexp').then(function (response) {
            self.setState({dets:response.data.message})
        })
    }

    render(){
        

        const menuCollapse = this.state.menuCollapse;
        const {dets} = this.state;
        console.log(dets)
        if(dets.length == 0){
            return <div><p>Hold Up!Data is loading</p></div>
        }
        return (
            <div id ="header">
              <ProSidebar collapsed={menuCollapse}>
          <SidebarHeader>
          <div className="logotext">
              {/* small and big change using menucollapse state */}
              <p>{menuCollapse ? "Logo" : "Big Logo"}</p>
            </div>
            <div className="closemenu" onClick={this.menuIconClick}>
                {/* changing menu collapse icon on click */}
              {menuCollapse ? (
                <FiArrowRightCircle/>
              ) : (
                <FiArrowLeftCircle/>
              )}
            </div>
          </SidebarHeader>
          <SidebarContent>
            <Menu iconShape="square">
              <MenuItem active={true} icon={<FiHome />}>
                Home
              </MenuItem>
              <MenuItem icon={<FaList />}>Category</MenuItem>
              <MenuItem icon={<FaRegHeart />}>Favourite</MenuItem>
              <MenuItem icon={<RiPencilLine />}>Author</MenuItem>
              <MenuItem icon={<BiCog />}>Settings</MenuItem>
            </Menu>
          </SidebarContent>
          <SidebarFooter>
            <Menu iconShape="square">
              <MenuItem icon={<FiLogOut />}>Logout</MenuItem>
            </Menu>
          </SidebarFooter>
        </ProSidebar>

            </div>
        );
        
    }
}
export default ViewExp;
