import React, {Component} from 'react'
class AdminLogin extends Component{
    constructor(){
        super();
        this.state = {
            uname : '',
            pword : ''
        };
    }
    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }
    

}
export default AdminLogin;
