            // const renderpdf = bytes.buffer
            console.log("successfull!!");
            // console.log(dets);
        })
        .catch(()=>
        {
          alert('Error fetching data');
        })
    }

    displayExp = (dets) =>{
      console.log(dets);
      // if(!dets.length) return null;
      

