import React, { Component } from 'react';
import logo from "./img/logo2.png";
import { $ }  from 'react-jquery-plugin'
import axios from 'axios';
import {HashLink as Link} from 'react-router-hash-link';

class  Experience extends Component {

    componentDidMount() {
       
        // define( [ "jquery" ], function( $ ) {

       
  // Your code here


// toggle = document.querySelectorAll(".toggle")[0];
// nav = document.querySelectorAll("nav")[0];
// toggle_open_text = 'Menu';
// toggle_close_text = 'Close';

// toggle.addEventListener('click', function() {
// 	nav.classList.toggle('open');
	
//   if (nav.classList.contains('open')) {
//     toggle.innerHTML = toggle_close_text;
//   } else {
//     toggle.innerHTML = toggle_open_text;
//   }
// }, false);

// setTimeout(function(){
// 	nav.classList.toggle('open');	
// }, 800);


// $(".menu").click(function () {
//   $(this).parent().toggleClass("close");
// });

// why it doesn't work on firefox?


//kid animation
//GSAP variables

//Navbar


var nav = $('nav');
var line = $('<div />').addClass('line');
line.appendTo('.active');

var active = nav.find('.active');
var pos = 0;
var wid = 0;

if(active.length) {
  pos = active.position().left;
  wid = active.width();
  line.css({
    left: pos,
    width: wid
  });
}



nav.find(' li a').click(function(e) {
  e.preventDefault();
  if(!$(this).parent().hasClass('active') && !nav.hasClass('animate')) {
    
    nav.addClass('animate');

    var _this = $(this);

    nav.find('li').removeClass('active');

    var position = _this.parent().position();
    var width = _this.parent().width();

    if(position.left >= pos) {
      line.animate({
        width: ((position.left - pos) + width)
      }, 300, function() {
        line.animate({
          width: width,
          left: position.left
        }, 150, function() {
          nav.removeClass('animate');
        });
        _this.parent().addClass('active');
      });
    } else {
      line.animate({
        left: position.left,
        width: ((pos - position.left) + wid)
      }, 300, function() {
        line.animate({
          width: width
        }, 150, function() {
          nav.removeClass('animate');
        });
        _this.parent().addClass('active');
      });
    }

    pos = position.left;
    wid = width;
  }
});

    }
    constructor(){
        super();
        this.state = {
            uname:'',
            email:'',
            year: '',
            company: '',
            linkdin: '',
            selectedFile: ''

        };
    }

    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }
    onSubmit = (e) =>{
        e.preventDefault();
        const {uname,pword} = this.state;
        axios.post('http://localhost:4000/api/users/login-admin',{username:uname,password:pword}).then(
            function (response){
                if(response.status == 200){
                    window.location = "/adminverify"
                }
                try{
                    localStorage.set('authtoken',response.data.token)
                }
                catch(error){
                    console.log(error)
                }
            }
        )

    }

    render() { 
        const {uname,email,year,linkdin,selectedFile} = this.state;
        return ( 
            <div>
                <div class="container-fluid bg-light position-relative shadow">
      <nav 
        class="navbar navbar-expand-lg bg-light navbar-light py-3 py-lg-0 px-0 px-lg-5"
      >
        <a
        
          class="navbar-brand font-weight-bold text-secondary" style={{fontSize: `35px`}}
          
        >
          <img src={logo} style={{height: `80px`, width: `80px`, paddingBottom: `5px`}}></img>
          <span class="text-primary " style={{fontFamily: `Handlee`}}>CSEA Placement</span>
        </a>
        <button
          type="button"
          class="navbar-toggler"
          data-toggle="collapse"
          data-target="#navbarCollapse"
        >
          <span class="navbar-toggler-icon"></span>
        </button>
        <div
          class="collapse navbar-collapse justify-content-between"
          id="navbarCollapse"
        >
          <div class="navbar-nav font-weight-bold mx-auto py-0 " >
            
            {/* <!-- <nav>
              <ul>
                <li class="active"><a href="" style="text-decoration: none;">About</a></li>
                <li><a href="#tips" style="text-decoration: none;">Tips</a></li>
                <li><a href="Experiance.html" style="text-decoration: none;">Experiences</a></li>
                <li><a href="contact.html" style="text-decoration: none;">Contact</a></li>
                <li><a href="gallery.html" style="text-decoration: block;">Five</a></li>
              </ul>
            </nav> --> */}
           
            <li  class="active"><a href="index.html" class="nav-item nav-link active">Home</a></li>
            <li ><a class="nav-item nav-link"><Link style={{textDecoration: `none`}} to="#about">About</Link></a></li>
            <li ><a href="" class="nav-item nav-link" ><Link style={{textDecoration: `none`}} to="#about">Experience</Link></a></li>
            <li><a href="" class="nav-item nav-link"><Link style={{textDecoration: `none`}} to="#about">Tips</Link></a></li>
            <li><a href="" class="nav-item nav-link"><Link style={{textDecoration: `none`}} to="#about">Contact</Link></a></li>
           
         
            
            {/* <!-- <div class="nav-item dropdown">
              <a
                href="#"
                class="nav-link dropdown-toggle"
                data-toggle="dropdown"
                >Pages</a
              >
              <div class="dropdown-menu rounded-0 m-0">
                <a href="blog.html" class="dropdown-item">Blog Grid</a>
                <a href="single.html" class="dropdown-item">Blog Detail</a>
              </div>
            </div>
            <a href="contact.html" class="nav-item nav-link">Contact</a> -->  */}
          </div>
         
        </div>
      </nav>
    </div>
    {/* <!-- Navbar End --> */}

    


    <div class="container-fluid pt-5">
        <div class="container">
            <div class="text-center pb-2">
                <p class="section-title px-5"><span class="px-2">Get In Touch</span></p>
                <h1 class="mb-4">Contact Us For Any Query</h1>
            </div>
            <div class="row" >
                <div class="col-lg-7 mb-5">
                    <div class="contact-form">
                        <div id="success"></div>
                        <form name="sentMessage" id="contactForm" novalidate="novalidate">
                            <div class="control-group" >
                                <input  type="text" class="form-control" id="name" placeholder="Your Name" required="required" data-validation-required-message="Please enter your name" name="uname" value={uname} onChange={this.onChange}/>
                                <p class="help-block text-danger"></p>
                            </div>
                            <div class="control-group">
                                <input type="email" class="form-control" id="email" placeholder="Your Email" required="required" data-validation-required-message="Please enter your email" name="email" value={email} onChange={this.onChange}/>
                                <p class="help-block text-danger"></p>
                            </div>
                            <div class="control-group">
                                <input type="text" class="form-control" id="subject" placeholder="Year of Interview" required="required" data-validation-required-message="Please enter the year of interview" name="year" value={year} onChange={this.onChange} />
                                <p class="help-block text-danger"></p>
                            </div>
                            <div class="control-group">
                                <input class="form-control" rows="6" id="linkdin" placeholder="Linkdin" required="required" data-validation-required-message="Please enter your Linkdin Link" name="linkdin" value={linkdin} onChange={this.onChange}></input>
                                <p class="help-block text-danger"></p>
                            </div>
                            <div class="control-group">
                                <input class="form-control" rows="7" id="selectedFile" placeholder="Select File" required="required"  name="file" type="file" value={file} onChange={this.handleChange}></input>
                                
                                <p class="help-block text-danger"></p>
                            </div>
                            <div>
                                <button class="btn btn-primary py-2 px-4" type="submit" id="sendMessageButton">Send Message</button>
                            </div>
                        </form>
                    </div>
                </div>
                
            </div>
        </div>
    </div>
 
 {/* <!-- Footer Start --> */}
 <div
      class="container-fluid bg-secondary text-white mt-5 py-5 px-sm-3 px-md-5"
    >
      <div class="row pt-5">
        <div class="col-lg-3 col-md-6 mb-5">
          <a
            href=""
