// 	nav.classList.toggle('open');	
// }, 800);


// $(".menu").click(function () {
//   $(this).parent().toggleClass("close");
// });

// why it doesn't work on firefox?


//kid animation
//GSAP variables

//Navbar


var nav = $('nav');
var line = $('<div />').addClass('line');
line.appendTo('.active');

var active = nav.find('.active');
var pos = 0;
var wid = 0;

if(active.length) {
  pos = active.position().left;
  wid = active.width();
  line.css({
    left: pos,
    width: wid
  });
}



nav.find(' li a').click(function(e) {
  e.preventDefault();
  if(!$(this).parent().hasClass('active') && !nav.hasClass('animate')) {
    
    nav.addClass('animate');

    var _this = $(this);

    nav.find('li').removeClass('active');

    var position = _this.parent().position();
    var width = _this.parent().width();

    if(position.left >= pos) {
      line.animate({
        width: ((position.left - pos) + width)
      }, 300, function() {
        line.animate({
          width: width,
          left: position.left
        }, 150, function() {
          nav.removeClass('animate');
        });
        _this.parent().addClass('active');
      });
    } else {
      line.animate({
        left: position.left,
        width: ((pos - position.left) + wid)
      }, 300, function() {
        line.animate({
          width: width
        }, 150, function() {
          nav.removeClass('animate');
        });
        _this.parent().addClass('active');
      });
    }

    pos = position.left;
    wid = width;
  }
});

    }
    constructor(){
        super();
        this.state = {
            name:'',
            email:'',
            year: '',
            company: '',
            linkdin: '',
            selectedFile: ''

        };
    }

    onChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    }
    onSubmit = (e) =>{
        e.preventDefault();
        const {uname,pword} = this.state;
        // axios.post('http://localhost:4000/api/users/login-admin',{username:uname,password:pword}).then(
        //     function (response){
        //         if(response.status == 200){
        //             window.location = "/adminverify"
        //         }
        //         try{
        //             localStorage.set('authtoken',response.data.token)
        //         }
        //         catch(error){
        //             console.log(error)
        //         }
        //     }
        // )

    }

    render() { 
        return ( 
            <div>
                <div class="container-fluid bg-light position-relative shadow">
      <nav 
        class="navbar navbar-expand-lg bg-light navbar-light py-3 py-lg-0 px-0 px-lg-5"
      >
        <a
        
          class="navbar-brand font-weight-bold text-secondary" style={{fontSize: `35px`}}
          
        >
          <img src={logo} style={{height: `80px`, width: `80px`, paddingBottom: `5px`}}></img>
          <span class="text-primary " style={{fontFamily: `Handlee`}}>CSEA Placement</span>
        </a>
        <button
          type="button"
          class="navbar-toggler"
          data-toggle="collapse"
          data-target="#navbarCollapse"
        >
          <span class="navbar-toggler-icon"></span>
        </button>
        <div
          class="collapse navbar-collapse justify-content-between"
          id="navbarCollapse"
        >
          <div class="navbar-nav font-weight-bold mx-auto py-0 " >
            
            {/* <!-- <nav>
              <ul>
                <li class="active"><a href="" style="text-decoration: none;">About</a></li>
                <li><a href="#tips" style="text-decoration: none;">Tips</a></li>
                <li><a href="Experiance.html" style="text-decoration: none;">Experiences</a></li>
                <li><a href="contact.html" style="text-decoration: none;">Contact</a></li>
                <li><a href="gallery.html" style="text-decoration: block;">Five</a></li>
              </ul>
            </nav> --> */}
           
            <li  class="active"><a href="index.html" class="nav-item nav-link active">Home</a></li>
            <li ><a class="nav-item nav-link"><Link style={{textDecoration: `none`}} to="#about">About</Link></a></li>
            <li ><a href="" class="nav-item nav-link" ><Link style={{textDecoration: `none`}} to="#about">Experience</Link></a></li>
            <li><a href="" class="nav-item nav-link"><Link style={{textDecoration: `none`}} to="#about">Tips</Link></a></li>
            <li><a href="" class="nav-item nav-link"><Link style={{textDecoration: `none`}} to="#about">Contact</Link></a></li>
           
         
            
            {/* <!-- <div class="nav-item dropdown">
              <a
                href="#"
                class="nav-link dropdown-toggle"
                data-toggle="dropdown"
                >Pages</a
              >
              <div class="dropdown-menu rounded-0 m-0">
                <a href="blog.html" class="dropdown-item">Blog Grid</a>
                <a href="single.html" class="dropdown-item">Blog Detail</a>
              </div>
            </div>
            <a href="contact.html" class="nav-item nav-link">Contact</a> -->  */}
          </div>
         
        </div>
      </nav>
    </div>
    {/* <!-- Navbar End --> */}

    <div class="container-fluid bg-primary px-0 px-md-5 mb-5">
      {/* <!-- <video src="video-1.mp4" autoPlay loop muted /> --> */}
      <div class="row align-items-center px-3" >
        <div class="col-lg-6 text-center text-lg-left">
          <h4 class="text-white mb-4 mt-5 mt-lg-0">students Learning Center</h4>
          <h1 class="display-3 font-weight-bold text-white">
            A New Approach.
          </h1>
          <p class="text-white mb-4">
            On the next screen enter a name for your new skill. On this page you can Choose a model to add to your skill (Custom selected by default) and Choose a method to host your skill's backend resources (Provision your own selected by default). Leave the default options selected and click the Create Skill button.
          </p>
          {/* <!-- <a href="" class="btn btn-secondary mt-1 py-3 px-5">Learn More</a> --> */}
        </div>

        {/* <!-- kid animation --> */}
        
        <div class=" col-lg-6 text-center text-lg-right">
          
          {/* <!-- <img class="img-fluid "  src="img/header image3.png" alt="" />  --> */}
        </div>
      </div>
    </div>
 
            </div>
         );
    }
}
 
